import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import blogData from '../data/blogs.json';

const BlogDetails = () => {
    const { id } = useParams();
    const [blog, setBlog] = useState(null);

    useEffect(() => {
        // find blog by id
        const found = blogData.find(b => b.id === parseInt(id));
        setBlog(found);

        // scroll to top when opening a blog
        window.scrollTo(0, 0);
    }, [id]);

    if (!blog) {
        return (
            <div className="container content" style={{ textAlign: 'center', marginTop: '50px' }}>
                <h2>Oops! Blog not found.</h2>
                <Link to="/" style={{ color: 'var(--accent-color)' }}>Go back to home</Link>
            </div>
        );
    }

    const initial = blog.author ? blog.author.charAt(0) : '?';

    return (
        <div className="container content">
            <div style={{ maxWidth: '680px', margin: '50px auto' }}>
                <h1 style={{
                    fontFamily: 'var(--font-serif)',
                    fontSize: '42px',
                    marginBottom: '20px',
                    lineHeight: '1.2'
                }}>
                    {blog.title}
                </h1>

                <div className="author-profile" style={{ marginBottom: '30px' }}>
                    
                    <div className="author-avatar">{initial}</div>
                    <div className="blog-meta-container">
                        <div style={{ fontWeight: '500' }}>{blog.author}</div>
                        <div style={{ fontSize: '12px', color: 'rgba(25, 25, 25, 0.6)' }}>{blog.date}</div>
                    </div>
                </div>

                <div style={{
                    fontSize: '18px',
                    lineHeight: '1.6',
                    color: 'rgba(25, 25, 25, 0.8)',
                    fontFamily: 'var(--font-serif)'
                }}>
                    {blog.content}
                </div>

                <div style={{ marginTop: '50px', borderTop: '1px solid rgba(25,25,25,0.1)', paddingTop: '20px' }}>
                    <Link to="/" style={{ color: 'var(--accent-color)', textDecoration: 'none', fontWeight: '500' }}>
                        ‚Üê Back to all stories
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default BlogDetails;
