import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import BlogCard from '../components/BlogCard';
import blogData from '../data/blogs.json';

function Home() {
    const [blogs, setBlogs] = useState([]);

    useEffect(() => {
        setBlogs(blogData);
    }, []);

    return (
        <div className="home-page">
            <section className="hero">
                <div className="container">
                    <div className="hero-inner">
                        <div className="hero-content">
                            <h1 className="hero-title">Human<br />stories & ideas</h1>
                            <p className="hero-subtitle">A place to read, write, and deepen your understanding</p>
                            <Link to="/blog" className="btn-hero">Start reading</Link>
                        </div>
                        <div className="hero-image">
                            <img src="/assets/medium_hero_illustration.png" alt="Hero Illustration" />
                        </div>
                    </div>
                </div>
            </section>
        </div>
    );
}

export default Home;
