import React, { useState } from 'react';

const Contact = () => {
    // State to hold form input values
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        subject: '', // Added subject field
        message: ''
    });

    // State to manage feedback messages to the user (e.g., success, error)
    const [status, setStatus] = useState('');

    // Handles changes to any input field and updates the formData state
    const handleChange = (e) => {
        const { name, value } = e.target; // Destructure name and value from the event target
        setFormData(prev => ({ // Use functional update to ensure we're working with the latest state
            ...prev, // Spread existing form data
            [name]: value // Update the specific field that changed
        }));
    };

    // Handles the form submission
    const handleSubmit = (e) => {
        e.preventDefault(); // Prevent the default form submission behavior (page reload)

        // Basic client-side validation check
        if (!formData.name || !formData.email || !formData.message) {
            setStatus('Please fill in all required fields.'); // Set an error status
            return; // Stop the submission process
        }

        // Log form data to console (in a real app, this would be sent to a backend)
        console.log('Form submitted:', formData);

        // Simulate a successful submission
        setStatus('Thanks! Your message has been sent. I will get back to you soon.');
        // Clear the form fields after successful submission
        setFormData({ name: '', email: '', subject: '', message: '' });

        // Hide the status message after 5 seconds
        setTimeout(() => setStatus(''), 5000);
    };

    return (
        <div className="container content">
            <div className="contact">
                <h1>Contact Me</h1>
                <p style={{ color: 'rgba(25, 25, 25, 0.6)', marginBottom: '30px' }}>
                    Have a question or just want to say hi? Feel free to reach out.
                </p>

                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>Name *</label>
                        <input
                            type="text"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            placeholder="Your Name"
                        />
                    </div>

                    <div className="form-group">
                        <label>Email *</label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            placeholder="your.email@example.com"
                        />
                    </div>

                    {/* Subject field could be added here if needed, similar to name/email */}
                    {/* <div className="form-group">
                        <label>Subject</label>
                        <input 
                            type="text" 
                            name="subject" 
                            value={formData.subject} 
                            onChange={handleChange} 
                            placeholder="Subject of your message"
                        />
                    </div> */}

                    <div className="form-group">
                        <label>Message *</label>
                        <textarea
                            name="message"
                            rows="5"
                            value={formData.message}
                            onChange={handleChange}
                            placeholder="Write your message here..."
                        ></textarea>
                    </div>

                    {/* Display status messages (e.g., validation errors, success messages) */}
                    {status && (
                        <div style={{
                            marginBottom: '20px',
                            color: status.includes('sent') ? 'var(--accent-color)' : 'red', // Style based on message content
                            fontSize: '14px'
                        }}>
                            {status}
                        </div>
                    )}

                    <button type="submit" className="btn-hero" style={{ border: 'none', cursor: 'pointer' }}>
                        Send Message
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Contact;
