import { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';

function Navbar() {
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    const navigate = useNavigate();
    const location = useLocation();

    // Sync input with URL on load/change
    useEffect(() => {
        const params = new URLSearchParams(location.search);
        setSearchQuery(params.get('q') || '');
    }, [location.search]);

    // Close sidebars on route change
    useEffect(() => {
        setIsSidebarOpen(false);
        setIsDropdownOpen(false);
    }, [location.pathname]);

    const handleSearch = (e) => {
        const query = e.target.value;
        setSearchQuery(query);

        const params = new URLSearchParams(location.search);
        if (query) {
            params.set('q', query);
        } else {
            params.delete('q');
        }

        if (location.pathname !== '/') {
            navigate(`/?${params.toString()}`);
        } else {
            navigate({ search: params.toString() }, { replace: true });
        }
    };

    return (
        <>
            <nav className="navbar">
                <div className="container">
                    <div className="nav-left">
                        <div className="hamburger" onClick={() => setIsSidebarOpen(true)}>‚ò∞</div>
                        <Link to="/" className="brand">Medium</Link>
                    </div>

                    <div className="nav-right">
                        <div className="search-container">
                            <span className="search-icon">üîç</span>
                            <input
                                type="text"
                                className="search-bar"
                                placeholder="Search"
                                value={searchQuery}
                                onChange={handleSearch}
                            />
                        </div>

                        <div className="profile-container" onClick={() => setIsDropdownOpen(!isDropdownOpen)}>
                            <div className="profile-icon">
                                <span>P</span>
                            </div>

                            {isDropdownOpen && (
                                <div className="dropdown-menu">
                                    <Link to="/" className="dropdown-item">Home</Link>
                                    <Link to="/contact" className="dropdown-item">Contact</Link>
                                    <Link to="/" className="dropdown-item">Blogs</Link>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </nav>

            {/* Sidebar Menu */}
            <div className={`sidebar-overlay ${isSidebarOpen ? 'active' : ''}`} onClick={() => setIsSidebarOpen(false)}></div>
            <div className={`sidebar ${isSidebarOpen ? 'active' : ''}`}>
                <div className="sidebar-header">
                    <div className="close-btn" onClick={() => setIsSidebarOpen(false)}>‚úï</div>
                </div>
                <div className="sidebar-links">
                    <Link to="/" className="sidebar-link">Blogs</Link>
                    <Link to="/contact" className="sidebar-link">Contact</Link>
                </div>
            </div>
        </>
    );
}

export default Navbar;
