import { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';

function Navbar() {
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    const navigate = useNavigate();
    const location = useLocation();

    // Sync input with URL on load/change
    useEffect(() => {
        const params = new URLSearchParams(location.search);
        setSearchQuery(params.get('q') || '');
    }, [location.search]);

    const handleSearch = (e) => {
        const query = e.target.value;
        setSearchQuery(query);

        const params = new URLSearchParams(location.search);
        if (query) {
            params.set('q', query);
        } else {
            params.delete('q');
        }

        // Navigate to home with query if not already there, or just update search
        if (location.pathname !== '/') {
            navigate(`/?${params.toString()}`);
        } else {
            navigate({ search: params.toString() }, { replace: true });
        }
    };

    return (
        <nav className="navbar">
            <div className="container">
                <div className="nav-left">
                    <div className="hamburger">‚ò∞</div>
                    <Link to="/" className="brand">Medium</Link>
                </div>

                <div className="nav-right">
                    <div className="search-container">
                        <span className="search-icon">üîç</span>
                        <input
                            type="text"
                            className="search-bar"
                            placeholder="Search"
                            value={searchQuery}
                            onChange={handleSearch}
                        />
                    </div>

                    <div className="profile-container" onClick={() => setIsDropdownOpen(!isDropdownOpen)}>
                        <div className="profile-icon">
                            <span>P</span>
                        </div>

                        {isDropdownOpen && (
                            <div className="dropdown-menu">
                                <Link to="/" className="dropdown-item">Home</Link>
                                <Link to="/contact" className="dropdown-item">Contact</Link>
                                <Link to="/" className="dropdown-item">Blogs</Link>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </nav>
    );
}

export default Navbar;
