// Navbar component for the blog site
import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';

const Navbar = () => {
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');

    const navigate = useNavigate();
    const location = useLocation();

    // keep search input in sync with URL
    useEffect(() => {
        const params = new URLSearchParams(location.search);
        setSearchQuery(params.get('q') || '');
    }, [location.search]);

    // close side menus when user clicks a link
    useEffect(() => {
        setIsSidebarOpen(false);
        setIsDropdownOpen(false);
    }, [location.pathname]);

    const onSearchChange = (e) => {
        const value = e.target.value;
        setSearchQuery(value);

        const params = new URLSearchParams(location.search);
        if (value) {
            params.set('q', value);
        } else {
            params.delete('q');
        }

        // redirect to home if searching from elsewhere
        if (location.pathname !== '/') {
            navigate(`/?${params.toString()}`);
        } else {
            navigate({ search: params.toString() }, { replace: true });
        }
    };

    const toggleProfileMenu = () => {
        setIsDropdownOpen(!isDropdownOpen);
    };

    return (
        <>
            <nav className="navbar">
                <div className="container">
                    <div className="nav-left">
                        {/* Hamburger icon for mobile menu */}
                        <div className="hamburger" onClick={() => setIsSidebarOpen(true)}>‚ò∞</div>
                        <Link to="/" className="brand">Medium</Link>
                    </div>

                    <div className="nav-right">
                        <div className="search-container">
                            <span className="search-icon">üîç</span>
                            <input
                                type="text"
                                className="search-bar"
                                placeholder="Search stories..."
                                value={searchQuery}
                                onChange={onSearchChange}
                            />
                        </div>

                        <div className="profile-container" onClick={toggleProfileMenu}>
                            <div className="profile-icon">S</div>

                            {isDropdownOpen && (
                                <div className="dropdown-menu">
                                    <Link to="/" className="dropdown-item">Home</Link>
                                    <Link to="/contact" className="dropdown-item">Contact</Link>
                                    <Link to="/" className="dropdown-item">Blogs</Link>
                                    
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </nav>

            {/* Side drawer for mobile/small screens */}
            <div
                className={`sidebar-overlay ${isSidebarOpen ? 'active' : ''}`}
                onClick={() => setIsSidebarOpen(false)}
            ></div>

            <div className={`sidebar ${isSidebarOpen ? 'active' : ''}`}>
                <div className="sidebar-header">
                    <span className="close-btn" onClick={() => setIsSidebarOpen(false)}>‚úï</span>
                </div>
                <div className="sidebar-links">
                    <Link to="/" className="sidebar-link">Blogs</Link>
                    <Link to="/contact" className="sidebar-link">Contact</Link>
                    
                </div>
            </div>
        </>
    );
};

export default Navbar;
