import { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import BlogCard from '../components/BlogCard';
import blogData from '../data/blogs.json';

function Home() {
    const [blogs, setBlogs] = useState([]);
    const location = useLocation();

    useEffect(() => {
        const query = new URLSearchParams(location.search).get('q')?.toLowerCase() || '';

        if (!query) {
            setBlogs(blogData);
        } else {
            const filtered = blogData.filter(blog =>
                blog.title.toLowerCase().includes(query) ||
                blog.author.toLowerCase().includes(query)
            );
            setBlogs(filtered);
        }
    }, [location.search]);

    return (
        <div className="home-page container content">
            <h1 className="hero-title" style={{ fontSize: '48px', marginBottom: '40px', marginTop: '40px' }}>
                {new URLSearchParams(location.search).get('q') ? 'Search Results' : 'Blogs'}
            </h1>
            <div className="blog-list">
                {blogs.length > 0 ? (
                    blogs.map(blog => (
                        <BlogCard key={blog.id} blog={blog} />
                    ))
                ) : (
                    <div className="no-blogs">No blogs found matching your search.</div>
                )}
            </div>
        </div>
    );
}

export default Home;
