import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import BlogCard from '../components/BlogCard';
import blogData from '../data/blogs.json';

const Home = () => {
    const [blogs, setBlogs] = useState([]);
    const [activeCategory, setActiveCategory] = useState('All');
    const location = useLocation();

    // Get unique categories from blog data
    const categories = ['All', ...new Set(blogData.map(blog => blog.category))];

    // Load and filter blogs whenever the query param or active category changes
    useEffect(() => {
        const params = new URLSearchParams(location.search);
        const q = params.get('q')?.toLowerCase() || '';

        let filtered = blogData;

        // Filter by category
        if (activeCategory !== 'All') {
            filtered = filtered.filter(blog => blog.category === activeCategory);
        }

        // Filter by search query
        if (q) {
            filtered = filtered.filter(blog =>
                blog.title.toLowerCase().includes(q) ||
                blog.author.toLowerCase().includes(q)
            );
        }

        setBlogs(filtered);
    }, [location.search, activeCategory]);

    return (
        <div className="home-page container content">
            <h1 className="hero-title" style={{ fontSize: '32px', marginBottom: '20px' }}>
                {new URLSearchParams(location.search).get('q') ? 'Search Results' : 'Blogs'}
            </h1>

            <div className="category-menu">
                {categories.map(category => (
                    <button
                        key={category}
                        className={`category-btn ${activeCategory === category ? 'active' : ''}`}
                        onClick={() => setActiveCategory(category)}
                    >
                        {category}
                    </button>
                ))}
            </div>

            <div className="blog-list">
                {blogs.length > 0 ? (
                    blogs.map(blog => (
                        <BlogCard key={blog.id} blog={blog} />
                    ))
                ) : (
                    <div className="no-blogs">
                        <p>No blogs found matching your search. Try something else!</p>
                    </div>
                )}
            </div>

            {/* TODO: Add pagination if list gets too long */}
        </div>
    );
};


export default Home;
