import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import BlogCard from '../components/BlogCard';
import blogData from '../data/blogs.json';

const Home = () => {
    const [blogs, setBlogs] = useState([]);
    const location = useLocation();

    
    useEffect(() => {
        const params = new URLSearchParams(location.search);
        const q = params.get('q')?.toLowerCase() || '';

        if (!q) {
            setBlogs(blogData);
        } else {
            const filtered = blogData.filter(blog =>
                blog.title.toLowerCase().includes(q) ||
                blog.author.toLowerCase().includes(q)
            );
            setBlogs(filtered);
        }
    }, [location.search]);

    return (
        <div className="home-page container content">
            <h1 className="hero-title" style={{ fontSize: '32px', marginBottom: '30px' }}>
                {new URLSearchParams(location.search).get('q') ? 'Search Results' : 'Blogs'}
            </h1>

            <div className="blog-list">
                {blogs.length > 0 ? (
                    blogs.map(blog => (
                        <BlogCard key={blog.id} blog={blog} />
                    ))
                ) : (
                    <div className="no-blogs">
                        <p>No blogs found matching your search. Try something else!</p>
                    </div>
                )}
            </div>

            {/* TODO: Add pagination if list gets too long */}
        </div>
    );
};

export default Home;
